// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

contract PythStructs {
    // A price with a degree of uncertainty, represented as a price +- a confidence interval.
    //
    // The confidence interval roughly corresponds to the standard error of a normal distribution.
    // Both the price and confidence are stored in a fixed-point numeric representation,
    // `x * (10^expo)`, where `expo` is the exponent.
    //
    // Please refer to the documentation at https://docs.pyth.network/documentation/pythnet-price-feeds/best-practices for how
    // to how this price safely.
    struct Price {
        // Price
        int64 price;
        // Confidence interval around the price
        uint64 conf;
        // Price exponent
        int32 expo;
        // Unix timestamp describing when the price was published
        uint publishTime;
    }

    // PriceFeed represents a current aggregate price from pyth publisher feeds.
    struct PriceFeed {
        // The price ID.
        bytes32 id;
        // Latest available price
        Price price;
        // Latest available exponentially-weighted moving average price
        Price emaPrice;
    }

    struct TwapPriceFeed {
        // The price ID.
        bytes32 id;
        // Start time of the TWAP
        uint64 startTime;
        // End time of the TWAP
        uint64 endTime;
        // TWAP price
        Price twap;
        // Down slot ratio represents the ratio of price feed updates that were missed or unavailable
        // during the TWAP period, expressed as a fixed-point number between 0 and 1e6 (100%).
        // For example:
        //   - 0 means all price updates were available
        //   - 500_000 means 50% of updates were missed
        //   - 1_000_000 means all updates were missed
        // This can be used to assess the quality/reliability of the TWAP calculation.
        // Applications should define a maximum acceptable ratio (e.g. 100000 for 10%)
        // and revert if downSlotsRatio exceeds it.
        uint32 downSlotsRatio;
    }

    // Information used to calculate time-weighted average prices (TWAP)
    struct TwapPriceInfo {
        // slot 1
        int128 cumulativePrice;
        uint128 cumulativeConf;
        // slot 2
        uint64 numDownSlots;
        uint64 publishSlot;
        uint64 publishTime;
        uint64 prevPublishTime;
        // slot 3
        int32 expo;
    }
}

contracts/ChainDepositContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./DepositManager.sol";
import "./PriceOracle.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title ChainDepositContract
 * @dev Deployed per chain to accept user deposits
 * Creates cross-chain intents via Avail Nexus
 * Implements "Bridge & Execute" pattern
 */
contract ChainDepositContract is ReentrancyGuard, Ownable {
    DepositManager public immutable depositManager;
    PriceOracle public immutable priceOracle;

    // Supported tokens for deposits
    mapping(address => bool) public supportedTokens;
    mapping(address => uint256) public tokenDecimals;

    // Deposit tracking
    struct Deposit {
        uint256 id;
        address user;
        address token;
        uint256 amount;
        uint256 btcEquivalent;
        uint256 timestamp;
        bool processed;
    }

    mapping(uint256 => Deposit) public deposits;
    mapping(address => uint256[]) public userDeposits;

    uint256 public nextDepositId = 1;
    uint256 public totalDeposits = 0;
    uint256 public totalBtcEquivalent = 0;

    event DepositCreated(
        uint256 indexed depositId,
        address indexed user,
        address indexed token,
        uint256 amount,
        uint256 btcEquivalent
    );

    event IntentCreated(
        uint256 indexed depositId,
        uint256 activationId,
        string intentData
    );

    event BtcEquivalentCalculated(
        address indexed token,
        uint256 amount,
        uint256 btcEquivalent
    );

    constructor(
        address _depositManager,
        address _priceOracle
    ) {
        depositManager = DepositManager(_depositManager);
        priceOracle = PriceOracle(_priceOracle);
    }

    /**
     * @dev Add supported token for deposits
     */
    function addSupportedToken(address token, uint256 decimals) external onlyOwner {
        supportedTokens[token] = true;
        tokenDecimals[token] = decimals;
    }

    /**
     * @dev Remove supported token
     */
    function removeSupportedToken(address token) external onlyOwner {
        supportedTokens[token] = false;
        tokenDecimals[token] = 0;
    }

    /**
     * @dev Deposit tokens and create cross-chain intent
     */
    function deposit(
        address token,
        uint256 amount
    ) external nonReentrant returns (uint256) {
        require(supportedTokens[token], "Token not supported");
        require(amount > 0, "Amount must be positive");

        // Transfer tokens from user
        IERC20(token).transferFrom(msg.sender, address(this), amount);

        // Calculate BTC equivalent using current price
        uint256 btcEquivalent = calculateBtcEquivalent(token, amount);

        // Create deposit record
        uint256 depositId = nextDepositId++;
        deposits[depositId] = Deposit({
            id: depositId,
            user: msg.sender,
            token: token,
            amount: amount,
            btcEquivalent: btcEquivalent,
            timestamp: block.timestamp,
            processed: false
        });

        userDeposits[msg.sender].push(depositId);
        totalDeposits++;
        totalBtcEquivalent += btcEquivalent;

        emit DepositCreated(depositId, msg.sender, token, amount, btcEquivalent);

        return depositId;
    }

    /**
     * @dev Create cross-chain intent for deposit
     * Implements "Bridge & Execute" pattern
     */
    function createIntent(uint256 depositId) external onlyOwner returns (uint256) {
        require(depositId < nextDepositId, "Invalid deposit ID");
        require(!deposits[depositId].processed, "Already processed");

        Deposit storage depositData = deposits[depositId];
        depositData.processed = true;

        // Send deposit to central manager
        uint256 activationId = depositManager.receiveDeposit(
            depositData.user,
            depositData.amount,
            depositData.btcEquivalent
        );

        // Create intent data for Avail Nexus
        string memory intentData = createIntentData(depositData, activationId);

        emit IntentCreated(depositId, activationId, intentData);

        return activationId;
    }

    /**
     * @dev Calculate BTC equivalent for token amount
     */
    function calculateBtcEquivalent(address token, uint256 amount) public returns (uint256) {
        // Get current BTC price from oracle
        uint256 btcPrice = priceOracle.getBtcPrice();

        // Get token decimals
        uint256 tokenDecimalsValue = tokenDecimals[token];
        if (tokenDecimalsValue == 0) {
            tokenDecimalsValue = 18; // Default to 18 decimals
        }

        // Calculate equivalent (simplified for demo)
        // In production, this would use actual price feeds
        uint256 btcEquivalent = (amount * btcPrice) / (10**tokenDecimalsValue);

        emit BtcEquivalentCalculated(token, amount, btcEquivalent);

        return btcEquivalent;
    }

    /**
     * @dev Create intent data for Avail Nexus
     */
    function createIntentData(Deposit memory depositData, uint256 activationId) internal pure returns (string memory) {
        return string(abi.encodePacked(
            '{"type":"bridge_execute",',
            '"depositId":', uint2str(depositData.id), ',',
            '"activationId":', uint2str(activationId), ',',
            '"user":"', addressToString(depositData.user), '",',
            '"token":"', addressToString(depositData.token), '",',
            '"amount":', uint2str(depositData.amount), ',',
            '"btcEquivalent":', uint2str(depositData.btcEquivalent),
            '}'
        ));
    }

    /**
     * @dev Get deposit details
     */
    function getDeposit(uint256 depositId) external view returns (Deposit memory) {
        return deposits[depositId];
    }

    /**
     * @dev Get user's deposits
     */
    function getUserDeposits(address user) external view returns (uint256[] memory) {
        return userDeposits[user];
    }

    /**
     * @dev Get contract statistics
     */
    function getStats() external view returns (
        uint256 totalDepositsCount,
        uint256 totalBtcEquivalentAmount,
        uint256 nextDepositIdValue
    ) {
        totalDepositsCount = totalDeposits;
        totalBtcEquivalentAmount = totalBtcEquivalent;
        nextDepositIdValue = nextDepositId;
    }

    /**
     * @dev Emergency withdraw (only owner)
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        IERC20(token).transfer(owner(), amount);
    }

    // Helper functions
    function uint2str(uint256 value) internal pure returns (string memory) {
        if (value == 0) return "0";
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }

    function addressToString(address addr) internal pure returns (string memory) {
        return uint2str(uint256(uint160(addr)));
    }
}

contracts/DepositManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./PriceOracle.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title DepositManager
 * @dev Central consolidation contract for cross-chain deposits
 * Manages sequential activation queue with price locking
 */
contract DepositManager is ReentrancyGuard, Ownable {
    PriceOracle public immutable priceOracle;

    struct Activation {
        uint256 id;
        address user;
        uint256 amount;
        uint256 btcEquivalent;
        uint256 lockedPrice;
        uint256 timestamp;
        bool processed;
        bool settled;
    }

    mapping(uint256 => Activation) public activations;
    mapping(address => uint256[]) public userActivations;

    uint256 public nextActivationId = 1;
    uint256 public currentProcessingId = 0;
    uint256 public btcPoolBalance = 0;

    // Sequential processing state
    bool public processingPaused = false;
    uint256 public maxQueueSize = 1000;

    event DepositReceived(
        uint256 indexed activationId,
        address indexed user,
        uint256 amount,
        uint256 btcEquivalent,
        uint256 lockedPrice
    );

    event ActivationProcessed(
        uint256 indexed activationId,
        uint256 newPrice,
        bool settled
    );

    event SettlementFailed(uint256 indexed activationId);
    event BtcPoolUpdated(uint256 newBalance);

    constructor(address _priceOracle) {
        priceOracle = PriceOracle(_priceOracle);
    }

    /**
     * @dev Receive deposit from chain deposit contract
     * Locks current price for sequential processing
     */
    function receiveDeposit(
        address user,
        uint256 amount,
        uint256 btcEquivalent
    ) external onlyOwner nonReentrant returns (uint256) {
        require(!processingPaused, "Processing paused");
        require(nextActivationId <= maxQueueSize, "Queue full");

        uint256 activationId = nextActivationId++;
        uint256 lockedPrice = priceOracle.getCurrentPrice();

        activations[activationId] = Activation({
            id: activationId,
            user: user,
            amount: amount,
            btcEquivalent: btcEquivalent,
            lockedPrice: lockedPrice,
            timestamp: block.timestamp,
            processed: false,
            settled: false
        });

        userActivations[user].push(activationId);

        emit DepositReceived(activationId, user, amount, btcEquivalent, lockedPrice);

        return activationId;
    }

    /**
     * @dev Process next activation in sequence
     * Only processes if previous activation is settled
     */
    function processNextActivation() external onlyOwner nonReentrant {
        require(!processingPaused, "Processing paused");
        require(currentProcessingId < nextActivationId, "No activations to process");

        uint256 activationId = currentProcessingId + 1;
        Activation storage activation = activations[activationId];

        require(!activation.processed, "Already processed");

        // Check if previous activation is settled (except for first activation)
        if (activationId > 1) {
            require(activations[activationId - 1].settled, "Previous activation not settled");
        }

        // Process activation
        activation.processed = true;
        priceOracle.activateToken();

        // Update BTC pool balance
        btcPoolBalance += activation.btcEquivalent;

        emit ActivationProcessed(activationId, priceOracle.getCurrentPrice(), false);
    }

    /**
     * @dev Mark activation as settled
     * Reverts if settlement fails
     */
    function settleActivation(uint256 activationId, bool settlementSuccess) external onlyOwner {
        require(activations[activationId].processed, "Not processed");

        if (!settlementSuccess) {
            emit SettlementFailed(activationId);
            revert("Settlement failed");
        }

        activations[activationId].settled = true;
        currentProcessingId = activationId;

        emit ActivationProcessed(activationId, priceOracle.getCurrentPrice(), true);
    }

    /**
     * @dev Get activation details
     */
    function getActivation(uint256 activationId) external view returns (Activation memory) {
        return activations[activationId];
    }

    /**
     * @dev Get user's activations
     */
    function getUserActivations(address user) external view returns (uint256[] memory) {
        return userActivations[user];
    }

    /**
     * @dev Get queue status
     */
    function getQueueStatus() external view returns (
        uint256 totalActivations,
        uint256 processedActivations,
        uint256 settledActivations,
        bool isProcessingPaused
    ) {
        totalActivations = nextActivationId - 1;
        processedActivations = currentProcessingId;
        settledActivations = currentProcessingId;
        isProcessingPaused = processingPaused;
    }

    /**
     * @dev Pause/resume processing
     */
    function setProcessingPaused(bool paused) external onlyOwner {
        processingPaused = paused;
    }

    /**
     * @dev Set max queue size
     */
    function setMaxQueueSize(uint256 newSize) external onlyOwner {
        maxQueueSize = newSize;
    }

    /**
     * @dev Update BTC pool balance (for external BTC conversions)
     */
    function updateBtcPoolBalance(uint256 newBalance) external onlyOwner {
        btcPoolBalance = newBalance;
        emit BtcPoolUpdated(newBalance);
    }

    /**
     * @dev Emergency function to reset processing state
     */
    function emergencyReset() external onlyOwner {
        currentProcessingId = 0;
        processingPaused = false;
    }
}

contracts/PriceOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@pythnetwork/pyth-sdk-solidity/IPyth.sol";
import "@pythnetwork/pyth-sdk-solidity/PythStructs.sol";

/**
 * @title PriceOracle
 * @dev Implements XMBL token economics with Pyth oracle integration
 * Price calculation: x / (Phi * y) where Phi = Golden Ratio (1.618...)
 * All prices round UP to nearest satoshi
 */
contract PriceOracle {
    // Golden ratio constant: (1 + sqrt(5)) / 2 â‰ˆ 1.618
    uint256 public constant PHI = 1618; // 1.618 * 1000 for precision

    uint256 public constant STARTING_PRICE = 1; // 1 satoshi
    uint256 public constant NETWORK_FEE_PERCENT = 3; // 3%
    uint256 public constant SATOSHI_PRECISION = 1e8; // 8 decimal places for satoshi

    IPyth public immutable pyth;
    bytes32 public immutable btcUsdPriceId;

    uint256 public currentPrice = STARTING_PRICE;
    uint256 public tokensMinted = 0;
    uint256 public activationCount = 0;

    mapping(uint256 => uint256) public activationPrices;
    mapping(uint256 => bool) public activationSettled;

    event PriceUpdated(uint256 newPrice, uint256 tokensMinted);
    event ActivationProcessed(uint256 activationId, uint256 price, bool settled);
    event SettlementFailed(uint256 activationId);

    constructor(address _pyth, bytes32 _btcUsdPriceId) {
        pyth = IPyth(_pyth);
        btcUsdPriceId = _btcUsdPriceId;
    }

    /**
     * @dev Calculate token price using golden ratio formula: x / (Phi * y)
     * Always rounds UP to nearest satoshi
     */
    function calculatePrice(uint256 _tokensMinted) public pure returns (uint256) {
        if (_tokensMinted == 0) return STARTING_PRICE;

        // Calculate: 1 / (Phi * tokensMinted)
        // Using PHI = 1618 (1.618 * 1000)
        uint256 numerator = 1000; // 1 * 1000 for precision
        uint256 denominator = (PHI * _tokensMinted) / 1000;

        if (denominator == 0) return STARTING_PRICE;

        uint256 price = numerator / denominator;

        // Round UP to nearest satoshi
        if (price % SATOSHI_PRECISION != 0) {
            price = ((price / SATOSHI_PRECISION) + 1) * SATOSHI_PRECISION;
        }

        return price;
    }

    /**
     * @dev Update price based on current token supply
     */
    function updatePrice() public {
        uint256 newPrice = calculatePrice(tokensMinted);
        currentPrice = newPrice;
        emit PriceUpdated(newPrice, tokensMinted);
    }

    /**
     * @dev Increase price on activation
     */
    function activateToken() external {
        activationCount++;
        tokensMinted++;
        updatePrice();
        emit ActivationProcessed(activationCount, currentPrice, false);
    }

    /**
     * @dev Decrease price on deactivation
     */
    function deactivateToken() external {
        if (tokensMinted > 0) {
            tokensMinted--;
            updatePrice();
        }
    }

    /**
     * @dev Process activation with settlement verification
     * Reverts if settlement fails
     */
    function processActivationWithSettlement(bool settlementSuccess) external {
        require(settlementSuccess, "Settlement failed");

        activationCount++;
        tokensMinted++;
        updatePrice();

        activationPrices[activationCount] = currentPrice;
        activationSettled[activationCount] = true;

        emit ActivationProcessed(activationCount, currentPrice, true);
    }

    /**
     * @dev Calculate 3% network fee, rounded UP to nearest satoshi
     */
    function calculateNetworkFee(uint256 amount) public pure returns (uint256) {
        uint256 fee = (amount * NETWORK_FEE_PERCENT) / 100;

        // Round UP to nearest satoshi
        if (fee % SATOSHI_PRECISION != 0) {
            fee = ((fee / SATOSHI_PRECISION) + 1) * SATOSHI_PRECISION;
        }

        return fee;
    }

    /**
     * @dev Get current price
     */
    function getCurrentPrice() external view returns (uint256) {
        return currentPrice;
    }

    /**
     * @dev Set tokens minted (for testing)
     */
    function setTokensMinted(uint256 _tokensMinted) external {
        tokensMinted = _tokensMinted;
        updatePrice();
    }

    /**
     * @dev Get BTC price from Pyth oracle
     */
    function getBtcPrice() external view returns (uint256) {
        // For now, return a mock price. In production, this would use Pyth's getPrice
        return 50000 * 1e8; // $50,000 in satoshis
    }
}

